
public class _05_UsoConstantes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//para declarar una constante usamos palabra final
		
		final int ejemplo=9; //mejor declarar e incializar
		
		final double pi=3.1416;
		
		System.out.println(ejemplo + pi);
		
		
		//programa que pase de pulgadas a cent√≠metros
		
		final double cmsenpulgada=2.54;
		
		int pulgadas=7;
		
		double resultado= pulgadas*cmsenpulgada;
		
		System.out.println(resultado);
		
		
	}

}
